// Get current month name
const date = new Date();
const monthNames = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
const currentMonth = monthNames[date.getMonth()];
const currentYear = date.getFullYear();

// Get KPI data
const kpis = $node["Get Financial KPIs"].json.data;

// Get cash flow data
const cashFlow = $node["Get Cash Flow Forecast"].json.data;

// Get anomalies
const anomalies = $node["Get Anomalies"].json.data.anomalies;

// Format KPI section
let kpiSection = `## Key Financial Indicators\n\n`;
kpiSection += `| Metric | Current | Previous | Change |\n`;
kpiSection += `|--------|---------|----------|--------|\n`;

if (kpis.profitability) {
  kpiSection += `| Gross Profit Margin | ${(kpis.profitability.grossProfitMargin.current * 100).toFixed(1)}% | ${(kpis.profitability.grossProfitMargin.previous * 100).toFixed(1)}% | ${kpis.profitability.grossProfitMargin.change > 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.profitability.grossProfitMargin.change * 100).toFixed(1)}% |\n`;
  kpiSection += `| Net Profit Margin | ${(kpis.profitability.netProfitMargin.current * 100).toFixed(1)}% | ${(kpis.profitability.netProfitMargin.previous * 100).toFixed(1)}% | ${kpis.profitability.netProfitMargin.change > 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.profitability.netProfitMargin.change * 100).toFixed(1)}% |\n`;
}

if (kpis.liquidity) {
  kpiSection += `| Current Ratio | ${kpis.liquidity.currentRatio.current.toFixed(2)} | ${kpis.liquidity.currentRatio.previous.toFixed(2)} | ${kpis.liquidity.currentRatio.change > 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.liquidity.currentRatio.change).toFixed(2)} |\n`;
  kpiSection += `| Quick Ratio | ${kpis.liquidity.quickRatio.current.toFixed(2)} | ${kpis.liquidity.quickRatio.previous.toFixed(2)} | ${kpis.liquidity.quickRatio.change > 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.liquidity.quickRatio.change).toFixed(2)} |\n`;
}

if (kpis.efficiency) {
  kpiSection += `| Accounts Receivable Days | ${kpis.efficiency.daysReceivablesOutstanding.current.toFixed(1)} | ${kpis.efficiency.daysReceivablesOutstanding.previous.toFixed(1)} | ${kpis.efficiency.daysReceivablesOutstanding.change < 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.efficiency.daysReceivablesOutstanding.change).toFixed(1)} |\n`;
  kpiSection += `| Accounts Payable Days | ${kpis.efficiency.daysPayablesOutstanding.current.toFixed(1)} | ${kpis.efficiency.daysPayablesOutstanding.previous.toFixed(1)} | ${kpis.efficiency.daysPayablesOutstanding.change > 0 ? 'â†‘' : 'â†“'} ${Math.abs(kpis.efficiency.daysPayablesOutstanding.change).toFixed(1)} |\n`;
}

// Format cash flow section
let cashFlowSection = `## Cash Flow Forecast\n\n`;
cashFlowSection += `Starting Balance: $${cashFlow.startingBalance.toFixed(2)}\n\n`;
cashFlowSection += `Projected Net Cash Flow (90 days): $${cashFlow.netCashFlow.toFixed(2)}\n\n`;
cashFlowSection += `### Monthly Projections\n\n`;
cashFlowSection += `| Month | Inflows | Outflows | Net Cash Flow | Ending Balance |\n`;
cashFlowSection += `|-------|---------|----------|--------------|----------------|\n`;

for (const month of cashFlow.monthlyForecasts) {
  cashFlowSection += `| ${month.month}/${month.year} | $${month.totalInflow.toFixed(2)} | $${month.totalOutflow.toFixed(2)} | $${month.netCashFlow.toFixed(2)} | $${month.endingBalance.toFixed(2)} |\n`;
}

// Format anomalies section
let anomaliesSection = '';
if (anomalies && anomalies.length > 0) {
  anomaliesSection = `## Financial Anomalies\n\n`;

  for (const anomaly of anomalies) {
    anomaliesSection += `### ${anomaly.type.replace(/_/g, ' ')}\n\n`;
    anomaliesSection += `**Severity**: ${anomaly.severity}\n\n`;
    anomaliesSection += `**Description**: ${anomaly.description}\n\n`;

    if (anomaly.date) {
      anomaliesSection += `**Date**: ${anomaly.date}\n\n`;
    } else if (anomaly.period) {
      anomaliesSection += `**Period**: ${anomaly.period.fromDate} to ${anomaly.period.toDate}\n\n`;
    }
  }
} else {
  anomaliesSection = `## Financial Anomalies\n\nNo significant anomalies detected in the past month.\n\n`;
}

// Format recommendations
let recommendationsSection = `## Recommendations\n\n`;

if (kpis.healthScore < 70) {
  recommendationsSection += `- **Improve Financial Health**: Your overall financial health score is ${kpis.healthScore}/100. Focus on improving liquidity and profitability.\n`;
}

if (kpis.efficiency && kpis.efficiency.daysReceivablesOutstanding.current > 45) {
  recommendationsSection += `- **Accelerate Collections**: Your accounts receivable days (${kpis.efficiency.daysReceivablesOutstanding.current.toFixed(1)}) are high. Consider implementing stricter payment terms or offering early payment discounts.\n`;
}

if (cashFlow.lowestBalance < 5000) {
  recommendationsSection += `- **Monitor Cash Flow**: Your projected lowest cash balance is $${cashFlow.lowestBalance.toFixed(2)} on ${cashFlow.lowestBalanceDate}. Consider delaying non-essential expenses or accelerating collections.\n`;
}

if (anomalies && anomalies.length > 0) {
  recommendationsSection += `- **Address Anomalies**: Review and address the ${anomalies.length} financial anomalies detected this month.\n`;
}

// Combine all sections
const reportTitle = `# Monthly Financial Report: ${currentMonth} ${currentYear}\n\n`;
const executiveSummary = `## Executive Summary\n\nFinancial Health Score: ${kpis.healthScore}/100 (${kpis.healthScore >= 80 ? 'Excellent' : kpis.healthScore >= 70 ? 'Good' : kpis.healthScore >= 60 ? 'Fair' : 'Needs Improvement'})\n\nGross Profit Margin: ${(kpis.profitability.grossProfitMargin.current * 100).toFixed(1)}%\n\nCurrent Ratio: ${kpis.liquidity.currentRatio.current.toFixed(2)}\n\nProjected Cash Position (90 days): $${(cashFlow.startingBalance + cashFlow.netCashFlow).toFixed(2)}\n\n`;

const fullReport = reportTitle + executiveSummary + kpiSection + '\n\n' + cashFlowSection + '\n\n' + anomaliesSection + '\n\n' + recommendationsSection;

return { report: fullReport };
